mapper.select:
select

from t_ds_process_definition_log
where code = #{code}
and version = #{version}

select max(version)
from t_ds_process_definition_log
where code = #{code}

select

from t_ds_process_definition_log
where code = #{code}

    and project_code = #{projectCode}

order by version desc

mapper.delete:
delete
from t_ds_process_definition_log
where code = #{code}
  and version = #{version}
