mapper.select:
SELECT td.id, td.name, td.version, td.release_state, td.project_id, td.user_id, td.description, td.global_params, td.flag, td.receivers, td.receivers_cc, td.timeout, td.tenant_id, td.modify_by, td.update_time, td.create_time, sc.schedule_release_state, tu.user_name FROM t_ds_process_definition td left join (select process_definition_id,release_state as schedule_release_state from t_ds_schedules group by process_definition_id,release_state) sc on sc.process_definition_id = td.id left join t_ds_user tu on td.user_id = tu.id where td.project_id = #{projectId}
and td.name like concat('%', #{searchVal}, '%')
and td.user_id = #{userId}
order by sc.schedule_release_state desc,td.update_time desc

mapper.update:
update  t_ds_process_definition
set version = #{version}
where id = #{processDefinitionId}
