mapper.select:
select

from t_ds_process_instance
where id = #{processId}

select
from t_ds_process_instance where 1=1
and host=#{host}
and state in
#{i}
order by id asc

select distinct host from t_ds_process_instance
where state in
#{i}

select

from t_ds_process_instance
where state = #{status}

    and project_code = #{projectCode}

and start_time between
#{startTime} and #{endTime}
order by end_time-start_time desc
limit #{size}

select
from t_ds_process_instance where 1=1
and tenant_code =#{tenantCode}
and state in
#{i}
order by id asc

select
from t_ds_process_instance where 1=1
and worker_group =#{workerGroupName}
and state in
#{i}
order by id asc

select

from t_ds_process_instance
where is_sub_process=0
and project_code = #{projectCode}

    and process_definition_code = #{processDefinitionCode}


    and name like concat('%', #{searchVal}, '%')


    and start_time  >=  #{startTime}


    and start_time  <=  #{endTime}


    and state in

        #{i}



    and host like concat('%', #{host}, '%')


    and executor_name = #{executorName}

order by start_time desc, end_time desc

mapper.update:
update t_ds_process_instance
set host=null
where host =#{host}

    and state in

        #{i}

update t_ds_process_instance
set state = #{destState}
where state = #{originState}

update t_ds_process_instance
set tenant_code = #{destTenantCode}
where tenant_code = #{originTenantCode}

update t_ds_process_instance
set worker_group = #{destWorkerGroupName}
where worker_group = #{originWorkerGroupName}

mapper.select:
select t.state, count(0) as count from t_ds_process_instance t join t_ds_process_definition d on d.code=t.process_definition_code where t.is_sub_process = 0
and t.start_time >= #{startTime} and t.start_time <= #{endTime}
and d.project_code in
#{i}
group by t.state

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode}
order by start_time desc limit #{size}

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode} and test_flag=#{testFlag}

    and schedule_time  >=  #{startTime} and schedule_time  <=  #{endTime}

order by end_time desc limit 1

select
from t_ds_process_instance where process_definition_code=#{processDefinitionCode} and test_flag=#{testFlag}
and state in
#{i}
and ((schedule_time >= #{startTime} and schedule_time <= #{endTime}) or (start_time >= #{startTime} and start_time <= #{endTime}))
order by start_time desc limit 1

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode} and test_flag=#{testFlag}
and schedule_time is null

    and start_time  >=  #{startTime} and start_time  <=  #{endTime}

order by end_time desc limit 1

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode}

    and state in

        #{i}


order by id asc

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode}
and process_definition_version = #{processDefinitionVersion}

    and state in

        #{i}


and next_process_instance_id=0

    and id  <  #{id}

order by id desc

select

from t_ds_process_instance
where process_definition_code=#{processDefinitionCode}
and state = #{state}
and id  >  #{id}
order by id asc limit 1

SELECT

FROM t_ds_process_instance
where is_sub_process=0

    and project_code = #{projectCode}


    and process_definition_code = #{processDefinitionCode}


    and name like concat('%', #{name}, '%')


    and start_time  >=  #{startTime}


    and start_time  <=  #{endTime}


    and state = #{state}


    and host like concat('%', #{host}, '%')

order by start_time desc, id desc

select t.state, count(0) as count
from t_ds_process_instance t
join t_ds_process_definition d on d.code=t.process_definition_code
join t_ds_project p on p.code=d.project_code
where 1 = 1
and t.is_sub_process = 0

    and p.id in

        #{i}



    and t.start_time  >=  #{startTime} and t.start_time  <=  #{endTime}


   and p.code = #{projectCode}


    and d.code = #{workflowCode}

group by t.state

mapper.update:
update t_ds_process_instance
set global_params = #{globalParams}
where id = #{id}

update t_ds_process_instance
set next_process_instance_id = #{thisInstanceId}
where id = #{runningInstanceId}
  and next_process_instance_id = 0
