mapper.select:
select max(version)
from t_ds_task_definition_log
WHERE code = #{code}

select

from t_ds_task_definition_log
WHERE code = #{code}
and version = #{version}

mapper.delete:
delete
from t_ds_task_definition_log
where code = #{code}
  and version = #{version}

mapper.select:
