mapper.select:
select



from t_ds_task_definition td
join t_ds_process_task_relation ptr on ptr.project_code = td.project_code
where td.project_code = #{projectCode}
and td.name = #{name}
and ptr.process_definition_code = #{processCode}
and td.code = ptr.post_task_code
limit 1

select td.name task_name,td.code task_code,td.version task_version,td.task_type,td.create_time task_create_time,td.update_time task_update_time, pd.code process_definition_code,pd.version process_definition_version,pd.name process_definition_name,pd.release_state process_release_state, pt.pre_task_code upstream_task_code,up.name upstream_task_name from t_ds_task_definition td LEFT JOIN t_ds_process_task_relation pt ON td.code = pt.post_task_code and td.version=pt.post_task_version LEFT JOIN t_ds_process_definition pd ON pt.process_definition_code = pd.code and pt.process_definition_version=pd.version LEFT JOIN t_ds_task_definition up on pt.pre_task_code=up.code and pt.pre_task_version=up.version WHERE td.project_code = #{projectCode}
and td.task_type = #{taskType}
and td.task_execute_type = #{taskExecuteType.code}
and pd.name like concat('%', #{searchWorkflowName}, '%')
and td.name like concat('%', #{searchTaskName}, '%')
order by td.update_time desc

SELECT

FROM t_ds_task_definition


        and project_code = #{task.projectCode}


        and name like concat('%', #{task.name}, '%')


        and task_type = #{task.taskType}


order by update_time desc, id asc
